import './App.css';
import React ,{useState}from 'react'
import { CURLParser } from 'parse-curl-js'


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      query: null,
      data:null,
    };

    this.publish = this.publish.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {
    this.setState({
      [target.name]: target.value
    });
  }

  publish() {
    console.log( this.state.query);
    const cURLParser = new CURLParser(this.state.query)
    console.log(cURLParser.parse());
    console.log(JSON.stringify(cURLParser.parse().body.data));
    const requestOptions = {
      method: cURLParser.parse().method,
      headers:  {'Accept': 'application/json',
      'Access-Control-Allow-Origin' : '*',
      'Access-Control-Allow-Methods':'GET, POST, PATCH, PUT, DELETE, OPTIONS',},
      body: JSON.stringify(cURLParser.parse().body.data),
      };

    fetch(cURLParser.parse().url, requestOptions)
    .then((data) => console.log(data))
    .catch((error) => console.log(error))
  };

  render() {
    return <div>
      <input 
        type="text" 
        name="query" 
        placeholder="Enter curl query here..." 
        value={ this.state.query }
        onChange={ this.handleChange } 
      />
      <button value="Send" onClick={ this.publish }>Run Query</button>
    </div>
  }
}

export default App;
